var util = require('util');
var express = require('express');
var router = express.Router();

var orderService = require('./services/orders');
var bot = require('./bot');
var botUtils = require('./bot/utils');

/* GET Checkout */
router.get('/', function (req, res, next) {
  // orderId and user address
  var orderId = req.query.orderId;
  var address = botUtils.deserializeAddress(req.query.address);
  console.log('user address is', address);

  orderService.retrieveOrder(orderId).then((order) => {
    // Check order exists
    if (!order) {
      throw new Error('Order ID not found');
    }

    // Check order if order is already processed
    if (order.payed) {
      // Dispatch completion dialog
      bot.beginDialog(address, 'checkout:/completed', { orderId: orderId });

      // Show completion
      return res.render('checkout/completed', {
        title: 'Contoso Flowers - Order Processed',
        order: order
      });
    }

    // Payment form
    return res.render('checkout/index', {
      title: 'Contoso Flowers - Order Checkout',
      address: req.query.address,
      order: order
    });

  }).catch((err) => {
    next(err);
  });

});

/* POST Checkout */
router.post('/', function (req, res, next) {
  // orderId and user address
  var orderId = req.body.orderId;
  var address = botUtils.deserializeAddress(req.body.address);
  console.log('user address is', address);

  // Payment information
  var paymentDetails = {
    creditcardNumber: req.body.creditcard,
    creditcardHolder: req.body.fullname
  };

  // Complete order
  orderService.confirmOrder(orderId, paymentDetails).then((processedOrder) => {

    // Dispatch completion dialog
    bot.beginDialog(address, 'checkout:/completed', { orderId: orderId });

    // Show completion
    return res.render('checkout/completed', {
      title: 'Contoso Flowers - Order processed',
      order: processedOrder
    });

  }).catch((err) => {
    next(err);
  });
});

module.exports = router;

var uuid = require('uuid');
var fs = require('fs');
var _ = require('lodash');
var Promise = require('bluebird');

const OrderService = {
    placePendingOrder: function (order) {
        order.id = uuid.v1();
        order.payed = false;

        var orders = this.load();
        orders.push(order);
        this.save(orders);

        return Promise.resolve(order);
    },
    retrieveOrder: function (orderId) {
        var orders = this.load();
        var order = _.find(orders, ['id', orderId]);

        return Promise.resolve(order);
    },
    confirmOrder: function (orderId, paymentDetails) {
        var orders = this.load();
        var order = _.find(orders, ['id', orderId]);
        if (!order) {
            return Promise.reject({ error: 'Order ID not found' });
        }

        if (order.payed) {
            return Promise.resolve(order);
        }

        order.payed = true;
        order.paymentDetails = paymentDetails;
        this.save(orders);

        return Promise.resolve(order);
    },

    // persistence
    load: function () {
        var json = fs.readFileSync('./data/orders.json', { encoding: 'utf8' });
        return JSON.parse(json);
    },
    save: function (orders) {
        var json = JSON.stringify(orders);
        fs.writeFileSync('./data/orders.json', json, { encoding: 'utf8' });
    }
};

module.exports = OrderService;

